import React, { useState } from "react";
import Button from "../UI/Button";
import TaskList from "./TaskList";
import Form from "./Form";

const Body = (props) => {
  const [isShowTasks, setIsShowTasks] = useState(false);
  const [isCreateTask, setIsCreateTask] = useState(false);
  const [isManageTask, setIsManageTask] = useState(false);
  const [selectedTask,setSelectedTask] = useState({});

  const showTaskHandler = () => {
    setIsShowTasks((prevState) => !prevState);
  };

  const createTaskHandler = () => {
    setIsCreateTask((prevState) => !prevState);
  };

  const manageTaskHandler = (task) => {
    setIsManageTask((prevState) => !prevState);
    setSelectedTask(task);
  };
  return (
    <div>
      <Button onClick={showTaskHandler}>Show tasks</Button>
      <Button onClick={createTaskHandler}>Create task</Button>
      {isManageTask && <Button>Delete task</Button>}
      {isManageTask && <Button>Update task</Button>}
      {isCreateTask && (
        <Form tasks={props.tasks} createTaskHandler={createTaskHandler} title={"Create Task"}/>
      )}
      {isShowTasks && (
        <TaskList manageTaskHandler={manageTaskHandler} tasks={props.tasks} />
      )}
      {isManageTask && (
        <Form tasks={props.tasks} manageTaskHandler={manageTaskHandler} title={"Update Task"} task={selectedTask}/>
      )}
    </div>
  );
};

export default Body;
