import React, { useState } from "react";
import "./Form.css";
import Button from "../UI/Button";
import FormInput from "./FormInput";
const Form = (props) => {
  const [values, setValues] = useState({
    id: "",
    projectName: "",
    TaskName: "",
    status: "",
  });

  const inputs = [
    {
      id: 1,
      name: "id",
      type: "number",
      placeholder: "Id",
      errorMessage: "Id should be a number",
      label: "Id",
      pattern: "^[0-9]{1,16}$",
      required: true,
    },
    {
      id: 2,
      name: "projectName",
      type: "text",
      placeholder: "project name",
      errorMessage: "This field cannot be left empty.",
      label: "Project name",
      pattern: "^[A-Za-z0-9]{1,16}$",
      required: true,
    },
    {
      id: 3,
      name: "taskName",
      type: "text",
      placeholder: "Task name",
      errorMessage: "This field cannot be left empty.",
      pattern: "^[A-Za-z0-9]{1,16}$",
      label: "Task name",
      required: true,
    },
    {
      id: 4,
      name: "status",
      type: "text",
      placeholder: "status",
      errorMessage: "You must choose a status for the task.",
      label: "status",
      pattern: "inactive" || "active" || "in-progress",
      required: true,
    },
  ];
  const handleSubmit = (e) => {
    e.preventDefault();
  };

  const onChange = (e) => {
    setValues({ ...values, [e.target.name]: e.target.value });
  };

  return (
    <div className="modal">
      <form onSubmit={handleSubmit} className="form">
        <h1>Create Task</h1>
        {inputs.map((input) => (
          <FormInput
            key={input.id}
            {...input}
            value={values[input.name]}
            onChange={onChange}
          />
        ))}
        <Button onClick={props.createTaskHandler}>Close form</Button>
      </form>
    </div>
  );
};

export default Form;
