import React,{useState} from 'react'
import './Form.css';
import Button from '../UI/Button';
import FormInput from './FormInput';
const Form = (props) => {

  const [values, setValues] = useState({
    id: "",
    projectName: "",
    TaskName: "",
    status: ""
  });

  const inputs = [
    {
      id: 1,
      name: "Id",
      type: "number",
      placeholder: "id",
      errorMessage:
        "Id should be a number",
      label: "id",
      pattern: "^[0-9]{1,16}$",
      required: true,
    },
    {
      id: 2,
      name: "Project name",
      type: "text",
      placeholder: "project name",
      label: "project name",
      required: true,
    },
    {
      id: 3,
      name: "Task name",
      type: "text",
      placeholder: "Task name",
      label: "Task name",
      required: true
    },
    {
      id: 4,
      name: "Status",
      type: "text",
      placeholder: "text",
      label: "status",
      pattern: "Inactive" || "Active" || "In progress",
      required: true,
    },
  ];
  const handleSubmit = (e) => {
    e.preventDefault();
  };

  const onChange = (e) => {
    setValues({ ...values, [e.target.name]: e.target.value });
  };

  return (
    <div className="modal">
        <form onSubmit={handleSubmit}>
        <h1>Register</h1>
        {inputs.map((input) => (
          <FormInput
            key={input.id}
            {...input}
            value={values[input.name]}
            onChange={onChange}
          />
        ))}
        <Button>Submit</Button>
      </form>
    </div>
  )
}

export default Form
