import React, { useState } from "react";
import Form from "./Form";
import "./Task.css";
const Task = (props) => {
  const [highlight, setHighlight] = useState("");

  const isClickedHandler = () => {
    setHighlight((prevState) => (prevState === "" ? "highlight" : ""));
    props.manageTaskHandler(props.task);
  };

  return (
    <React.Fragment>
      <tr className={`task ${highlight}`} onClick={isClickedHandler}>
        <td>{props.id}</td>
        <td>{props.projectName}</td>
        <td>{props.taskName}</td>
        <td>{props.status}</td>
      </tr>
      <Form title={"Create Task"} />
    </React.Fragment>
  );
};

export default Task;
